mapper.select:
select * from (select rownum rn, c.* from ( select u.uno, b.* from ( select a.my_id , nvl(a.chargedpoint,0) charge , nvl(a.partyattend,0) use , nvl(p.ptitle,'') title , a.this_regdate , a.remainpoint , a.this_no from (select * from iboughtthis ) a
left outer join party p on p.pno = a.your_pno) b left outer join users u on u.id = b.my_id order by this_no desc ) c where uno like '%' || #{searchName} || '%' 1=1
)
where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

select count(*) from ( select u.uno, b.* from ( select a.my_id , nvl(a.chargedpoint,0) charge , nvl(a.partyattend,0) use , nvl(p.ptitle,'') title , a.this_regdate , a.remainpoint from (select * from iboughtthis ) a
left outer join party p on p.pno = a.your_pno) b left outer join users u on u.id = b.my_id
) where uno like '%' || #{searchName} || '%' 1=1
